mapper.select:
select *
from stat_profit_total
        where 1

    and time  >=  #{start}


    and time  <=  #{end}

select *
from stat_profit_goods
        where 1

    and time  >=  #{start}


    and time  <=  #{end}

select *
from stat_finish_order
        where 1

    and time  >=  #{start}


    and time  <=  #{end}

select count(0)
from stat_finish_order
where time = #{time}

select count(0)
from stat_profit_total
where time = #{time}

select ifnull(sum(total), 0) total
from biz_purchase_order
        where status = 2

    and vtime  >=  #{start}


    and vtime  <=  #{end}

select ifnull(sum(total), 0) total
from biz_sell_order
        where status = 2

    and vtime  >=  #{start}


    and vtime  <=  #{end}
